<?php
/**
 * Stub generated by xlatte
 */
class PayMethod extends paymethodBase{

    /**
     * Gets the publicly available wallets (Payment Methods)
     *
     * @remote
     * @return PayMethod[]
     */
    public static function getPublicAvailable(){
        return DL::arrayOf("PayMethod,Wallet", "
            SELECT #COLUMNS 
            FROM paymethod
             INNER JOIN wallet USING (idwallet)
            WHERE enabled = 1
        ");
    }

    /**
     * Searches payments methods
     * @remote
     * @param string $text
     * @return PayMethod[]
     */
    public static function search($text){
        return DL::arrayOf('PayMethod', "
            SELECT #COLUMNS
            FROM payMethod
            WHERE name LIKE '%$text%'
        ");
    }

    /**
     * Wallet of method
     * @var Wallet
     */
    public $wallet;

}